//Global Variables
uint8_t current_task = 1;
uint32_t g_tick_count = 0;

typedef struct
{
	uint32_t psp_val;
	uint32_t block_count;
	uint32_t current_state;
	void (*task_handler)(void);
}TCB_t;

TCB_t user_tasks[MAX_TASKS];

int main(void)
{
	enable_processor_faults();
	initialise_monitor_handles();
	sched_stack_init(SCHDLR_STACK_START);
	task_stack_init();
	init_leds();
	systick_init(TICK_FREQ);
	switch_sp_to_psp();
	task1_handler();
	/* Loop forever */
	for(;;);
}

void schedule(void)
{
	uint32_t *pICSR = (uint32_t *)0xE000ED04;
	//Pend the PendSV bit in ICSR reg
	*pICSR |= (1<<28);
}

void task_delay(uint32_t tick_count)
{
	INTRPT_DISABLE();

	if(current_task)
	{
		user_tasks[current_task].block_count = g_tick_count + tick_count;
		user_tasks[current_task].current_state = TASK_BLOCKED;
		schedule();
	}

	INTRPT_ENABLE();
}
void task1_handler(void)
{
	while(1)
	{
		printf("In Task_1 Handler\n");
		led_on(LED_GREEN);
		task_delay(1000);
		led_off(LED_GREEN);
		task_delay(1000);
	}
}

