void systick_init(uint32_t tick_freq)
{
	uint32_t rl_val = ((CLOCK_FREQ / tick_freq) - 1);
	uint32_t *pSTK_CTRL = (uint32_t *)0xE000E010;
	uint32_t *pSTK_RLOAD = (uint32_t *)0xE000E014;
	uint32_t *pSTK_VAL = (uint32_t *)0xE000E018;
	//*pSTK_RLOAD &= ~(0x00FFFFFF);
	*pSTK_RLOAD |= rl_val; // Loading with count value for Down Counter in SysTick timer
	*pSTK_VAL &= ~(0xFFFFFF);
	*pSTK_CTRL |= ((1<<1) | (1<<2)); //Enabling SysTick exception and Choosing clock source
	*pSTK_CTRL |= (1<<0); //Enabling SYSTICK timer
}

__attribute__((naked)) void sched_stack_init(uint32_t schdlr_stack_start)
{
	__asm volatile("MSR MSP, %0": : "r"(schdlr_stack_start):);
	__asm volatile("BX LR");
}

void task_stack_init()
{
	uint32_t *pPSP;

	user_tasks[0].current_state = TASK_READY;
	user_tasks[1].current_state = TASK_READY;
	user_tasks[2].current_state = TASK_READY;
	user_tasks[3].current_state = TASK_READY;
	user_tasks[4].current_state = TASK_READY;

	user_tasks[0].psp_val = IDLE_STACK_START;
	user_tasks[1].psp_val = T1_STACK_START;
	user_tasks[2].psp_val = T2_STACK_START;
	user_tasks[3].psp_val = T3_STACK_START;
	user_tasks[4].psp_val = T4_STACK_START;

	user_tasks[0].task_handler = idle_handler;
	user_tasks[1].task_handler = task1_handler;
	user_tasks[2].task_handler = task2_handler;
	user_tasks[3].task_handler = task3_handler;
	user_tasks[4].task_handler = task4_handler;

	for (int i=0; i<MAX_TASKS; i++)
	{
		pPSP = (uint32_t *)user_tasks[i].psp_val;

		pPSP--;
		*pPSP = DUMMY_XPSR;

		pPSP--;
		*pPSP = (uint32_t )user_tasks[i].task_handler;

		pPSP--;
		*pPSP = LR_VAL;

		for (int j=0; j<13; j++)
		{
			pPSP--;
			*pPSP = 0;
		}

		user_tasks[i].psp_val = (uint32_t)pPSP;
	}
}

uint32_t get_psp_val(void)
{
	return user_tasks[current_task].psp_val;
}
__attribute__((naked)) void switch_sp_to_psp(void)
 {
	 __asm volatile("PUSH {LR}");
	 __asm volatile("BL get_psp_val");
	 __asm volatile("MSR PSP, R0");
	 __asm volatile("POP {LR}");

	 __asm volatile("MOV R0,#0x02");
	 __asm volatile("MSR CONTROL, R0");
	 __asm volatile("BX LR");
 }

void enable_processor_faults(void)
{
	uint32_t * pSHCSR = (uint32_t*) 0xE000ED24;
	__attribute__ ((unused))uint32_t * pUFSR = (uint32_t *) 0xE000ED2A;
	*pSHCSR |= (7 << 16);
}

