void save_psp(uint32_t current_psp_val)
{
	user_tasks[current_task].psp_val = current_psp_val;
}

void update_next_task(void)
{
	int state = TASK_BLOCKED;
	for (int i=0; i<(MAX_TASKS); i++)
	{
		current_task++;
		current_task %= MAX_TASKS;
		state = user_tasks[current_task].current_state;

		if((state == TASK_READY) && (current_task !=0))
			break;
	}

	if(state != TASK_READY)
		current_task = 0;
}

__attribute__ ((naked))void PendSV_Handler(void)
{
	//Saving context of current task
	__asm volatile("MRS R0, PSP");
	__asm volatile("STMDB R0!, {R4-R11}");
	__asm volatile("PUSH {LR}");
	__asm volatile("BL save_psp");

	//Loading context of next task
	__asm volatile("BL update_next_task");
	__asm volatile("BL get_psp_val");
	__asm volatile("LDMIA R0!, {R4-R11}");
	__asm volatile("MSR PSP, R0");
	__asm volatile("POP {LR}");
	__asm volatile("BX LR");
}
void SysTick_Handler(void)
{
	uint32_t *pICSR = (uint32_t *)0xE000ED04;
	update_g_tick_count();
	unblock_tasks();
	//Pend the PendSV bit in ICSR reg
	*pICSR |= (1<<28);

}
void update_g_tick_count(void)
{

	g_tick_count++;
}

void unblock_tasks(void)
{
	for (int i=0; i<MAX_TASKS; i++)
	{
		if (user_tasks[i].current_state != TASK_READY)
		{
			if(user_tasks[i].block_count == g_tick_count)
			{
				user_tasks[i].current_state = TASK_READY;
			}
		}
	}
}
